import { useState, useEffect } from "react";
import axios from "axios";

const App = () => {
  const [countries, setCountries] = useState([])
  const [countrySearch, setCountrySearch] = useState("")

  useEffect(() => {
    console.log("effect");
    axios.get('https://restcountries.com/v3.1/all').then(response => setCountries(response.data))
  }, [])

  const countriesToShow = countries.filter((country) => {
    return (country.name.common).toLowerCase().includes(countrySearch)
  })

  const handleCountrySearchChange = (event) => {
    setCountrySearch(event.target.value)
  }

  return (
    <div>
      find countries <input value={countrySearch} onChange={handleCountrySearchChange} />
      {
        countriesToShow.length > 10
          ? (<p>Too many matches, specify another filter</p> )
          : (
            countriesToShow.length > 1? countriesToShow.map(country => {
              return <p>{country.name.common}</p>
            }) : (
              countriesToShow.map(country => {
                return (
                  <div>
                    <h2>{country.name.common}</h2>
                    <p>capital {country.capital[0]}</p>
                    <p>area {country.area}</p>
                    <h3>languages: </h3>
                    <ul>
                      {Object.values(country.languages).map((lang) => <li>{lang}</li>)}
                    </ul>
                    {country.flag}
                  </div>
                )
              })
            )
          )
      }
    </div>
  )
}

export default App;
