const Persons = ({personsToShow, handleDelete}) => {
    return (    
        <div>
            {
                personsToShow.map((person) => {
                    return (
                            <p key={person.id}>
                                {person.name} {person.number} 
                                <button onClick={() => handleDelete(person.id)}>delete</button>
                            </p>
                    )
                })
            }
        </div>
    )
}


export default Persons;
