import { useState } from "react";
import SearchFilter from "./components/SearchFilter";
import NewPersonAdd from "./components/NewPersonAdd";

const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas', number: '040-123456', id: 1 },
    { name: 'Ada Lovelace', number: '39-44-5323523', id: 2 },
    { name: 'Dan Abramov', number: '12-43-234345', id: 3 },
    { name: 'Mary Poppendieck', number: '39-23-6423122', id: 4 }
  ])
  const [newPersonName, setNewPersonName] = useState('')
  const [newPersonNumber, setNewPersonNumber] = useState('')
  const [filterNames, setFilterNames] = useState('')

  const addNewPerson = (event) => {
    event.preventDefault();
    const newPerson = {
      name: newPersonName,
      number: newPersonNumber,
    }
    const ifExists = persons.some((person) => {
      return newPersonName === person.name
    })

    if(ifExists){
      alert(`${newPersonName} is already added to phonebook`)
      setNewPersonName("")
      setNewPersonNumber("")
    }else{
      setPersons(persons.concat(newPerson))
      setNewPersonName("")
      setNewPersonNumber("")
    }
  }

  const personsToShow = filterNames==="" 
    ? persons :  
    persons.filter(person => {
      console.log("position: ", person.name.indexOf(filterNames))
      const name = (person.name).toLowerCase()
      return name.includes(filterNames.toLowerCase())
    })

  const handleNameChange = (event) => {
    setNewPersonName(event.target.value)
  }

  const handleNumberChange = (event) => {
    setNewPersonNumber(event.target.value)
  }

  const handleFilterChange = (event) => {
    console.log(event.target.value);
    console.log(personsToShow);
    setFilterNames(event.target.value)
  }

  return (
    <div>
      <h2>Phonebook</h2>
      <SearchFilter filterNames={filterNames} handleFilterChange={handleFilterChange} />
      <h2>add a new</h2>
      {/* <form onSubmit={addNewPerson}>
        <div>name: <input value={newPersonName} onChange={handleNameChange} /></div>
        <div>number: <input value={newPersonNumber} onChange={handleNumberChange} /></div>
        <div>
          <button type="submit">add</button>
        </div>
      </form> */}
      <NewPersonAdd 
        value1={newPersonName} 
        value2={newPersonNumber} 
        handleNameChange={handleNameChange}
        handleNumberChange={handleNumberChange}
      />
      <h2>Numbers</h2>
      {personsToShow.map(person => <p key={person.id}>{person.name} {person.number}</p>)}
    </div>
  )
}

export default App;
